import React, { useState } from 'react';
import { Food_1, Food_2 } from '../../asset/images/restaurant_info/haidilao/food/index.js';
import { Buttons } from '../../components';

const Items = ({ type }) => {
  const [counter, setCounter] = useState(0);

  const increment = () => {
    setCounter(prevCount => prevCount + 1);
    console.log(counter + 1);
  }

  const decrement = () => {
    if(counter > 0) {
      setCounter(prevCount => prevCount - 1);
      console.log(counter - 1);
    }
  }

  switch(type) {
    case 'food_item':
      return (
        <div className="w-300 h-24 flex items-center border border-placeholders p-2 rounded-md">
          <Food_1 className="w-24 h-24 rounded-2xl" />
          <div className="flex flex-col flex-grow ml-2">
            <p className="text-xl font-semibold">Neapolitan pizza</p>
            <p className="text-sm text-gray-500">Always a picnic favorite</p>
            <p className="text-xl font-semibold">200,000đ</p>
          </div>
          <div className="ml-2">
            <Buttons context='plus' onClick={increment} />
          </div>
        </div>
      );
    case 'food_item_cart':
      return (
        <div className="w-72 h-16 flex items-center border border-placeholders p-2 rounded-md">
          <Food_2 className="w-16 h-16 rounded-3xl" />
          <div className="flex flex-col flex-grow ml-2">
            <p className="text-lg font-medium">Spicy fresh crab</p>
            <p className="text-sm text-gray-500 mb-1">No Spice</p>
            <div className="flex items-center">
              <Buttons context='edit' />
              <p className="text-xl font-medium ml-2">35,000đ</p>
            </div>
          </div>
          <div className="flex items-center">
            <Buttons context='minus' onClick={decrement} />
            <p className="mx-2">{counter}</p>
            <Buttons context='plus' onClick={increment} />
          </div>
        </div>
      );
    default:
      return null;
  }
};

export default Items;
