import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import { getOrder } from "../api";

export const fetchOrder = createAsyncThunk('order/fetchOrder', async (orderId) => {
  const response = await getOrder(orderId);
  return response.data;
});

const orderSlice = createSlice({
  name: 'order',
  initialState: { data: {}, status: 'not ready' },
  reducers: {
    setOrderStatus: (state, action) => {
      state.status = action.payload;
    }
  },
  extraReducers: {
    [fetchOrder.fulfilled]: (state, action) => {
      state.data = action.payload;
      if(action.payload.status === 'ready') {
        state.status = 'ready';
      }
    }
  }
});

export const { setOrderStatus } = orderSlice.actions;
export const selectOrder = state => state.order.data;
export const selectOrderStatus = state => state.order.status;

export default orderSlice.reducer;
