const ScanQR = () => {
  const [result, setResult] = useState('');

  const handleScan = data => {
    if (data) {
      setResult(data.text); // store only the text part of the data
      console.log("Scanned data: ", data);
      alert("Scanned data: " + data.text); // use data.text instead of data
    }
  }

  const handleError = err => {
    console.error(err);
  }

  return (
    <div className='flex flex-col items-center justify-start h-screen px-5 pt-20 space-y-6'>
      <div className='flex items-center justify-between w-full mb-5'>
        <Buttons context='back' className='mr-6' />
        <h1 className='text-black text-2xl'>Scan QR</h1>
      </div>
      <QrReader
        delay={300}
        style={{ width: '100%' }}
        onError={handleError}
        onScan={handleScan}
      />
      <p>{result}</p> {/* Now result is a string, not an object */}
      <Buttons context="scan_me"></Buttons>
    </div>
  );
};

export default ScanQR;
