import React, { useEffect, useState } from 'react';
import { CollapseIcon, CheckIcon, InProgressIcon } from '../../asset/icons/box/index.js';
import { Buttons } from '../../components';
import Progress from './Progress';
import './boxes.css'

const Boxes = ({ orderId, currentStep, buttonStatus }) => {
  const [orderData, setOrderData] = useState({});

  useEffect(() => {
    const fetchData = async () => {
      // Fetch the order data from your database/API
      // Assuming you have a function called getOrder that does this
      // const data = await getOrder(orderId); 

      // Update the state with the fetched data
      setOrderData(data);
    }

    // Call the fetchData function
    fetchData();
  }, [orderId]);  // The effect runs when the component mounts and whenever orderId changes

  return (
    <div className="box relative w-80 h-64 bg-tertiary overflow-hidden">
      <input type="checkbox" className="absolute top-0 inset-x-0 w-full h-12 opacity-0 z-10 cursor-pointer peer"/>

      <div className="header-content h-36 w-full pl-5 flex flex-col justify-center space-y-1 text-xs">
        <h1 className="font-semibold text-white">Order: #{orderData.order}</h1>
        <p className="text-white">Estimate Time: {orderData.estimatedTime} mins</p>
        <p className="text-white">Total: {orderData.total}</p>
        <p className="text-white">Food item(s): {orderData.items}</p>

        <div className="progress-bar-container w-4/5 mx-auto my-2">
          <Progress currentStep={currentStep} />
        </div>
      </div>

      <div className="absolute top-5 right-3 transition-transform duration-500 rotate-0 peer-checked:rotate-180">
        <CollapseIcon className="text-white" />
      </div>

      <div className="button-container w-full flex justify-center pb-2">
        <Buttons context="order" className={`mt-2 ${buttonStatus === 'ready' ? 'bg-primary' : 'bg-gray'}`}>Order Received</Buttons>
      </div>

      <div className="content bg-white overflow-hidden transition-all duration-500 max-h-0 peer-checked:max-h-40">
        <div className="p-4">
          content
        </div>
      </div>
    </div>
  );
}

export default Boxes;
