import { createSlice } from '@reduxjs/toolkit';

export const orderSlice = createSlice({
  name: 'order',
  initialState: {
    data: {},
    currentStep: 1,
    buttonStatus: 'not ready',
  },
  reducers: {
    setOrderData: (state, action) => {
      state.data = action.payload;
    },
    setCurrentStep: (state, action) => {
      state.currentStep = action.payload;
    },
    setButtonStatus: (state, action) => {
      state.buttonStatus = action.payload;
    },
  },
});

export const { setOrderData, setCurrentStep, setButtonStatus } = orderSlice.actions;

export const selectOrderData = (state) => state.order.data;
export const selectCurrentStep = (state) => state.order.currentStep;
export const selectButtonStatus = (state) => state.order.buttonStatus;

export default orderSlice.reducer;
