import React, { useState } from "react";
import { CheckIcon, InProgressIcon } from '../../asset/icons/box/index.js';

const Progress = () => {
  const steps = ["Confirmed", "In Progress", "Ready"];
  const [currentStep, setCurrentStep] = useState(1);
  const [complete, setComplete] = useState(false);

  const getStepIcon = (stepIndex) => {
    if (stepIndex + 1 < currentStep || complete) {
      return (
        <div className='w-full h-full bg-primary flex items-center justify-center rounded-sm'>
          <CheckIcon className='w-4 h-4'/>
        </div>
      );
    }
    if (stepIndex + 1 === currentStep && !complete) {
      return (
        <div className='w-full h-full bg-gray flex items-center justify-center rounded-sm'>
          <InProgressIcon className='w-4 h-4'/>
        </div>
      );
    }
    return stepIndex + 1;
  };

  return (
    <>
      <div className="flex justify-between">
        {steps?.map((step, i) => (
          <div
            key={i}
            className={`step-item w-10 h-10 border rounded-sm ${currentStep === i + 1 && "active"} ${
              (i + 1 < currentStep || complete) && "complete"
            } `}
          >
            <div className="step flex items-center justify-center">
              {getStepIcon(i)}
            </div>
            <p className="text-gray-500">{step}</p>
          </div>
        ))}
      </div>
    </>
  );
};

export default Progress;
