        <Buttons context='back' className='absolute top-5 left-5 z-10' onClick={handleBackClick}/>
        <Carousel showStatus={false} className="w-96 h-80">
          <div>
            <img src={Food_1} className='w-full h-full object-cover'/>
          </div>
          <div>
            <img src={Food_2} className='w-full h-full object-cover'/>
          </div>
        </Carousel>
      </div>

      {/* rest of the code ... */}

    </div>
  );
};

export default MenuDetail;
