import React, { useState } from 'react';
import { Buttons } from '../../components';
import { Food_1, Food_2 } from '../../asset/images/restaurant_info/haidilao/food/index.js';
import { Carousel } from 'react-responsive-carousel';
import 'react-responsive-carousel/lib/styles/carousel.min.css';

const MenuDetail = () => {
  const [foodItemNotes, setFoodItemNotes] = useState('');
  const [counter, setCounter] = useState(0);

  const handleChange = (event) => {
    setFoodItemNotes(event.target.value);
  };

  return (
    <div className='flex flex-col items-start justify-start h-screen px-5 pt-20 space-y-6'>
      <div className='flex items-center justify-between w-full mb-5'>
        <Buttons context='back' className='mr-6' />
      </div>

      <div className='w-3/4 h-3/4 flex justify-center'>
        <Carousel showStatus={false}>
          <div>
            <Food_1 className='w-full h-full object-cover'/>
          </div>
          <div>
            <Food_2 className='w-full h-full object-cover'/>
          </div>
        </Carousel>
      </div>

      <h2 className='text-3xl font-bold mt-6'>Fried Shrimp</h2>
      <p className='text-lg text-justify'>Most whole Alaskan Red King Crabs get broken down into legs, claws, and lump meat. We offer all of these options as well in our online shop, but there is nothing like getting the whole...</p>

      <label className='text-lg font-medium my-1'>Note to restaurant</label>
      <input
        className='px-2 text-l bg-gray opacity-60 text-black font-medium rounded-lg w-full h-300'
        type='text'
        name='foodItemNotes'
        onChange={handleChange}
        placeholder='Enter your notes here'
      />

      <div className='flex justify-between items-center w-full my-5'>
        <h2 className='text-3xl font-bold'>35.000 Ä‘</h2>

        <div className="flex items-center">
          <Buttons context='minus' count={counter} setCount={setCounter} />
          <p className="mx-2 text-xl">{counter}</p>
          <Buttons context='plus' count={counter} setCount={setCounter} />
        </div>
      </div>

      <div className="flex justify-center w-full">
        <Buttons context='add_to_cart'/>
      </div>
    </div>
  );
};

export default MenuDetail;
