import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { Buttons, Boxes, Popups } from '../../components';

const OnGoing = () => {
  const navigate = useNavigate();
  const [popupVisible, setPopupVisible] = useState(false);
  const [ongoingOrdersEmpty, setOngoingOrdersEmpty] = useState(true);

  const handleOnGoingClick = () => {
    navigate('/menu-overview');
  };

  const handleOrderReceivedClick = () => {
    setPopupVisible(true);
    if(popupVisible) {
      setOngoingOrdersEmpty(true);
    }
  };

  const closePopup = () => {
    setPopupVisible(false);
  };

  const handleScanQRClick = () => {
    navigate('/scanqr');
  };

  return (
    <div className="flex flex-col items-center px-5 py-20 md:px-10">
      <div className='flex items-center justify-between w-full mb-5'>
        <Buttons context='back' onClick={handleOnGoingClick}/>
        <h1 className='text-black text-2xl md:text-3xl'>On Going</h1>
      </div>

      {ongoingOrdersEmpty ? (
        <div className="flex flex-col items-center justify-center space-y-10">
          <h2 className="text-lg md:text-xl">There is no on-going order</h2>
          <Buttons context="scan_qr" onClick={handleScanQRClick}></Buttons>
        </div>
      ) : (
        <Boxes type='on-going' className='mb-2.5' onOrderReceived={handleOrderReceivedClick}/>
      )}

      <h2 className='text-black text-2xl md:text-3xl mb-5 pt-10 text-right'>History</h2>

      <Boxes type='history' className='mb-2.5'/>

      <Popups type="order_completed" visible={popupVisible} onClose={closePopup} />
    </div>
  );
};

export default OnGoing;
