import React, { useEffect } from 'react';
import { useSelector, useDispatch } from 'react-redux';
import { CollapseIcon, CheckIcon, InProgressIcon } from '../../asset/icons/box/index.js';
import { Buttons, Progress } from '../../components';
import './boxes.css';

const Boxes = ({ orderId, currentStep }) => {
  const dispatch = useDispatch();

  const orderData = useSelector(selectOrder);
  const buttonStatus = useSelector(selectOrderStatus);

  useEffect(() => {
    dispatch(fetchOrder(orderId)); 
  }, [dispatch, orderId]);  // The effect runs when the component mounts and whenever orderId changes

  useEffect(() => {
    if (currentStep >= 3) {  // If all steps are completed, the order is ready
      dispatch(setOrderStatus('ready'));
    }
  }, [currentStep, dispatch]);

  return (
    <div className="box relative w-300 h-150 bg-tertiary overflow-hidden">
      <input type="checkbox" className="absolute top-0 inset-x-0 w-full h-12 opacity-0 z-10 cursor-pointer peer"/>

      <div className="header-content h-36 w-full pl-5 flex flex-col justify-center items space-y-1 text-xs">
        <h1 className="font-semibold ">Order: #{orderData.order}</h1>
        <p className="">Estimate Time: {orderData.estimatedTime} mins</p>
        <p className="">Total: {orderData.total}</p>
        <p className="">Food item(s): {orderData.items}</p>

        <div className="progress-bar-container w-4/5 mx-auto my-2">
          <Progress currentStep={currentStep} />
        </div>
      </div>

      <div className="absolute top-5 right-3 transition-transform duration-500 rotate-0 peer-checked:rotate-180">
        <CollapseIcon className="text-white" />
      </div>

      <div className="button-container w-full flex justify-center pb-2">
        <Buttons context="order" className={`mt-2 ${buttonStatus === 'ready' ? 'bg-primary' : 'bg-gray'}`}>Order Received</Buttons>
      </div>

      <div className="content bg-white overflow-hidden transition-all duration-500 max-h-0 peer-checked:max-h-40">
        <div className="p-4">
          content
        </div>
      </div>
    </div>
  );
}

export default Boxes;
