import React from 'react';
import './boxes.css';
import { CollapseIcon, CheckIcon, InProgressIcon } from '../../asset/icons/box/index.js';
import { Buttons } from '../../components';

const Box = ({ order, estimatedTime, total, items, orderStatus, buttonStatus }) => {
  const getStatusIcon = (status) => {
    switch (status) {
      case "Confirmed":
      case "In Progress":
        return (
          <div className='w-full h-full bg-gray flex items-center justify-center rounded-sm'>
            <InProgressIcon className='w-4 h-4'/>
          </div>
        );
      case "Ready":
        return (
          <div className='w-full h-full bg-primary flex items-center justify-center rounded-sm'>
            <CheckIcon className='w-4 h-4'/>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div>
      <div className='relative w-80 h-64 bg-tertiary overflow-hidden'>
        <input type="checkbox" className='absolute top-0 inset-x-0 w-full h-12 opacity-0 z-10 cursor-pointer peer'/>
        <div className='h-24 w-full pl-5 flex flex-col justify-center space-y-1 text-sm'>
          <h1 className='font-semibold'>
            Order: #{order}
          </h1>
          <p>Estimate Time: {estimatedTime} mins</p>
          <p>Total: {total}</p>
          <p>Food item(s): {items}</p>
          <Buttons context='order' className={`mt-2 ${buttonStatus === 'ready' ? 'bg-primary' : 'bg-gray'}`}></Buttons>
        </div>

        {/* arrow icon */}
        <div className='absolute top-5 right-3 transition-transform duration-500 rotate-0 peer-checked:rotate-180'>
          <CollapseIcon></CollapseIcon>
        </div>

        {/* progress bar */}
        <div className="w-4/5 mx-auto my-2">
          <hr className="border border-primary" />
          <div className="flex justify-between mt-1">
            {["Confirmed", "In Progress", "Ready"].map(status => (
              <div key={status} className="w-10 h-10 flex items-center justify-center rounded-sm">
                {getStatusIcon(status)}
              </div>
            ))}
          </div>
        </div>

        {/* content */}
        <div className='bg-white overflow-hidden transition-all duration-500 max-h-0 peer-checked:max-h-40'>
          <div className='p-4'>
            Content will be here.
          </div>
        </div>
      </div>
    </div>
  )
}

export default Box;
