import React from 'react';
import { CheckIcon, InProgressIcon } from '../../asset/icons/box/index.js';

const steps = ['Confirmed', 'In Progress', 'Ready'];

const Step = ({ currentStepIndex, stepIndex, step }) => {
  const Icon = stepIndex <= currentStepIndex ? CheckIcon : InProgressIcon;
  const bgColor = stepIndex <= currentStepIndex ? 'bg-primary' : 'bg-gray';
  const textColor = stepIndex <= currentStepIndex ? 'text-white' : 'text-gray-500';

  return (
    <div className={`step flex flex-col items-center mx-10`}>
      <div className={`w-10 h-10 rounded-md ${bgColor} flex items-center justify-center`}>
        <Icon className={textColor} />
      </div>
      <p className={`text-sm text-center overflow-hidden truncate whitespace-nowrap ${textColor}`}>{step}</p>
    </div>
  );
}

const Progress = ({ currentStep }) => {
  const currentStepIndex = steps.indexOf(currentStep);

  return (
    <div className="h-full flex justify-center items-center pr-8">
      {steps.map((step, index) => (
        <div className='flex flex-col items-center'>
          <Step currentStepIndex={currentStepIndex} stepIndex={index} step={step} key={step} />
          {index < steps.length - 1 && 
          <div className={`w-px h-4 bg-gray-300 mx-5 ${currentStepIndex >= index + 1 ? 'bg-primary' : ''}`}></div>}
        </div>
      ))}
    </div>
  );
};

export default Progress;
