import React, { useEffect, useState } from 'react';
import { Buttons } from '../../components';
import './popup.css'

const Popups = ({visible, title, description, context, type, customStyles}) => {
  const [showPopup, setShowPopup] = useState(false);



  useEffect(() => {
    if (!visible) {
      setShowPopup(false);
    } else {
      setShowPopup(true);
    }
  }, [visible]);

  let contents;

  switch (type) {
    default:
      contents = (
        <>
          <h2 className='text-2xl font-bold mb-4'>Order Completed</h2>
          <p className='text-lg mb-3'>Register to save your order history</p>
          <Buttons context='on_going' className='mb-5' onClick={handlePaymentClick}></Buttons>
          <Buttons context='order_smth' onClick={handlePaymentClick}></Buttons>
        </>
      );
  }

  if (!showPopup) return null;

  return (
    <div className='fixed inset-0 bg-black bg-opacity-50 flex items-end justify-center z-10'>
      <div className={`bg-white px-6 py-4 rounded-t-3xl w-full text-center slide-up ${showPopup ? 'show' : ''}`}>
        {contents}
      </div>
    </div>
  );
}

export default Popups;
