import React from 'react';
import { CheckIcon, InProgressIcon } from '../../asset/icons/box/index.js';
import "./progress.css";

const steps = ['Confirmed', 'In Progress', 'Ready'];

const Progress = ({ currentStep }) => (
  <div className="w-full h-full flex justify-center items-center space-x-5">
    {steps.map((step, index) => (
      <div key={step} className={`step-container flex flex-col items-center ${index === 1 ? 'mx-auto' : ''}`}> 
        <div className={`step ${currentStep > index ? 'bg-primary' : 'bg-gray'} flex items-center justify-center`}>
          {currentStep > index ? <CheckIcon className='text-white' /> : <InProgressIcon className='text-white' />}
        </div>
        <p className="text-sm text-center">{step}</p>
      </div>
    ))}
  </div>
);

export default Progress;
