import React from 'react';
import { CheckIcon, InProgressIcon } from '../../asset/icons/box/index.js';

const steps = ['Confirmed', 'In Progress', 'Ready'];

const Step = ({ currentStep, index, step }) => {
  const Icon = currentStep >= index ? CheckIcon : InProgressIcon;
  const bgColor = currentStep >= index ? 'bg-primary' : 'bg-gray';

  return (
    <div className={`step flex flex-col items-center`}>
      <div className={`w-10 h-10 rounded-md ${bgColor} flex items-center justify-center`}>
        <Icon className='text-white' />
      </div>
      {index < steps.length - 1 && (
        <div className={`h-1 w-32 my-4 ${currentStep > index ? 'bg-primary' : 'bg-gray'}`}></div>
      )}
      <p className="text-sm text-center overflow-hidden truncate whitespace-nowrap">{step}</p>
    </div>
  );
}

const Progress = ({ currentStep }) => (
  <div className="h-full flex justify-between items-center">
    {steps.map((step, index) => (
      <Step currentStep={currentStep} index={index} step={step} key={step} />
    ))}
  </div>
);

export default Progress;
